import React  from 'react';
import './WeatherApp.css';

function WeatherMain(props) {

    const styles = {
        color: "white",
        fontSize: 50,
        fontWeight: 700,
        textShadow: "3px 3px rgba(50, 50, 70, 0.5)",
        textAlign: "center",
        justifyContent: "center"
      }

    return(
        <div styles={styles} >


            {props.description}
            {props.icon}

        </div>
    )
}
export default WeatherMain;
