import React  from 'react';
import './WeatherApp.css';

function WeatherMain(props) {

    const styles = {
        color: "white",
        fontSize: 50,
        fontWeight: 700,
        textShadow: "3px 3px rgba(50, 50, 70, 0.5)",
        textAlign: "center",
        justifyContent: center
      }

    return(
        <div style={{
            color: 'white',
            fontSize: '48px',
            fontWeight: 700,
            textShadow: "3px 3px rgba(50, 50, 70, 0.5)",
            textAlign: center,
            justifyContent: center}}>

             {props.description}
            <p> {props.icon} </p>

        </div>
    )
}
export default WeatherMain;
