import './WeatherApp.css';
import axios from 'axios';
import React, {useState} from 'react';

function WeatherApp() {

  const apiKey = 'a26c42b0ac5962d5bbc6665ebc491ac9';
  const [weatherData, setWeatherData] = useState([{}]);
  const [city, setCity] = useState("");


    const getWeather = (event) => {
     if (event.key === 'Enter') {}
     axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=imperial&cnt=7&APPID=${apiKey}`)
        .then(response => {
            console.log("AXIOS RESPONSE: ", response.data);
            setWeatherData(response.data)
         //   setCity("");
          })
          .catch(err => {
            console.log(err);
        })};


        let rows = [];
        if ( typeof weatherData.weather != 'undefined' ) {
          if (weatherData.weather[0].main === "Clouds") {
            let backgroundColor = 'red';
            return (
              <div className='container' style={{ backgroundColor: backgroundColor }}/>
            )
          }
           rows = weatherData.weather.map(el => {
              return (
                <ul>
                   <li>{el.description} </li>
               </ul>
              ) // return
        }) 
        }  



  return (
    <div className="container">
      <input className="input" placeholder="Enter City" 
      onChange={e => setCity(e.target.value)} value={city} onKeyPress={getWeather} />

     {typeof weatherData.main === 'undefined' ? (
       <div>
         <p>Welcome to weather app! Enter in a city to get the weather.</p>
       </div>
     ) : (
       <div>
         <p>{weatherData.name}</p>
         <p>{Math.round(weatherData.main.temp)}Â°F</p>
         <p>{weatherData.weather[0].main}</p>
       {rows} 
        </div>
     )}  

    </div>
  );
}

export default WeatherApp;
