import './WeatherApp.css';
import axios from 'axios';
import React, {useEffect, useState} from 'react';

function WeatherApp() {

  const apiKey = 'a26c42b0ac5962d5bbc6665ebc491ac9';
  const [weatherData, setWeatherData] = useState([{}]);
  const [city, setCity] = useState("");

    const getWeather = (event) => {
      axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&units=imperial&APPID=${apiKey}`)
        .then(response => {
            console.log("AXIOS RESPONSE: ", response.data);
            setWeatherData(response.data)
            setCity("");
          })
          .catch(err => {
            console.log(err);
        })};

  return (
    <div className="container">
      <input className="input" placeholder="Enter City" 
      onChange={e => setCity(e.target.value)} value={city} onKeyPress={getWeather} />

     {typeof weatherData.main === 'undefined' ? (
       <div>
         <p>Welcome to weather app! Enter in a city to get the weather.</p>
       </div>
     ) : (
       <div>
         <p>{weatherData.name}</p>
         <p>{Math.round(weatherData.main.temp)}</p>
         <p></p>
        </div>
     )}  

    </div>
  );
}

export default WeatherApp;
